export const StarRating = ({rating}) => {


   
    //    let inactiveColor='#ddd';
    //    let  size=15;
    //     let activeColor='#f00';
    const[stars, setStars] = useState([]);
    // // short trick 
    useEffect(()=>{
       setStars(Array.from({length: 5}));
       console.log(stars);
    })
    

    // Internal handle change function
    //   const handleChange = (value) => {
    //     onChange(value + 1);
    //   }

    return (
        <div>
        {stars.map((index) => {
            let style = "";
            if (index < rating) {
            style="active";
            }
            return (
            <span className={style}  
                key={index}>
                {/* onClick={()=>handleChange(index)}>{s} */}
                </span>
            )
        })}
        {rating}
        </div>
    )
    }
