import React, {useCallback, useContext, useEffect, useState} from 'react'
import { ProductsList } from '../components/ProductsList'
import {useHttp} from '../hooks/http.hook'

export const Products = () => {

  const {loading, request} = useHttp()

  const [productsss, setProducts] = useState([])

  const getproductsHandler = useCallback(async () => {

    try {
      const data = await request('/api/products/getproducts', 'GET')
      
      setProducts(data);
      
    } catch (e) {}

  },[request]);

  useEffect(() => {
    getproductsHandler();
    console.log(productsss);
  },[getproductsHandler])



  return (
    <>
        {<ProductsList products={productsss} />}
    </>
        // <div>
          
        //     {/* <h1>PRODUCTS page</h1>
        //     <button onClick={getproductsHandler}>Click</button> */}
        // </div>
        
  )
}
